import React, { useState } from 'react';

function SignIn() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState({});

    const handleEmailChange = (e) => setEmail(e.target.value);
    const handlePasswordChange = (e) => setPassword(e.target.value);

    const validate = () => {
        const error = {};
        if (!email) error.email = 'Email is required';
        if (!password) error.password = 'Password is required';
        if (email && !email.includes('@')) error.email = 'Invalid email';
        setError(error);
        return error;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const validationError = validate();
        if (Object.keys(validationError).length > 0) {
            setError(validationError);
        } else {
            console.log('Form submitted:', { email, password });
            setEmail('');
            setPassword('');
            setError({});
        }
    };

    return (
        <div className='signin'>
            <div className='signin-container'>
                <div className='sign-in'>
                    <img
                        src="https://leetcode.com/static/webpack_bundles/images/logo.c36eaf5e6.svg"
                        alt="Logo"
                    />
                </div>
                <form onSubmit={handleSubmit} className='Form'>
                    <div className='input-container'>
                        <div className='input-wrapper'>
                            {error.email && <span className='error'>{error.email}</span>}
                            <input
                                placeholder="Enter Your Email"
                                className='input-field'
                                type="email"
                                value={email}
                                onChange={handleEmailChange}
                            />
                            <label className='input-label'>Email</label>
                            <span className='input-highlight'></span>
                        </div>

                        <div className='input-wrapper'>
                            {error.password && <span className='error'>{error.password}</span>}
                            <input
                                placeholder="Enter Your Password"
                                className='input-field'
                                type="password"
                                value={password}
                                onChange={handlePasswordChange}
                            />
                            <label className='input-label'>Password</label>
                            <span className='input-highlight'></span>
                        </div>
                    </div>
                    <br />
                    <button type="submit" className='Sign-button'>
                        Log in
                    </button>
                </form>

                <div className='forget-password'>
                    <p>Forget Password</p>
                    <p>Sign Up</p>
                </div>
                <br />
                <div className='accounts'>
                    <p>or you can sign in with</p>
                    <div className='accounts-icon'>
                        <svg viewBox="0 0 24 24" width="30" height="30" opacity="0.4" className="icon__1Md2 icon__3F7K">
                            <path
                                fillRule="evenodd"
                                d="M12 22C6.477 22 2 17.523 2 12S6.477 2 12 2s10 4.477 10 10-4.477 10-10 10zm.044-5.213c2.445 0 4.267-1.551 4.556-3.781v-1.891h-4.519v1.89h2.602a2.893 2.893 0 0 1-2.724 1.93c-1.194 0-2.677-1.1-2.677-2.843 0-1.621 1.161-2.876 2.677-2.876.739 0 1.413.279 1.922.736l1.399-1.376a4.744 4.744 0 1 0-3.236 8.212z"
                            ></path>
                        </svg>
                        {/* Add other SVG icons here */}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default SignIn;
